import axios from "axios";
import React, { useEffect, useState } from "react";

const Card = ({ movie }) => {
  const [genreData, setGenreData] = useState([]);

  const dateFormater = (date) => {
    let newDate = new Date(date).toLocaleDateString("fr-FR", {
      year: "numeric",
      month: "long",
      day: "numeric",
    });
    return newDate;
  };

  const formattedDate = dateFormater(movie.release_date);

  useEffect(() => {
    axios
      .get(
        "https://api.themoviedb.org/3/genre/movie/list?api_key=ed82f4c18f2964e75117c2dc65e2161d"
      )
      .then((res) => setGenreData(res.data.genres))
      .catch((error) => console.error("Error fetching genre data:", error));
  }, []);

  const getGenreNameById = (id) => {
    const genre = genreData.find((genre) => genre.id === id);
    return genre ? genre.name : "Unknown";
  };

  const saveMovie = (e) => {
    e.preventDefault();
    let searchFavMovies = JSON.parse(localStorage.getItem("favMovies") || "[]");
    let savedMovies = {
      id: movie.id,
    };

    const isMovieAlreadySaved = searchFavMovies.some(
      (savedMovie) => savedMovie.id === savedMovies.id
    );

    if (movie.id.length != 0 && !isMovieAlreadySaved) {
      searchFavMovies.push(savedMovies);
      localStorage.setItem("favMovies", JSON.stringify(searchFavMovies));
    }
  };

  const addStorage = () => {
    let storedData = window.localStorage.movies
      ? window.localStorage.movies.split(",")
      : [];

    if (!storedData.includes(movie.id.toString())) {
      storedData.push(movie.id);
      window.localStorage.movies = storedData;
    }
  };

  return (
    <li className="card">
      <img
        src={
          movie.poster_path
            ? "https://image.tmdb.org/t/p/original/" + movie.poster_path
            : "/img/poster.jpg"
        }
        alt={"Affiche " + movie.title}
      />
      <h2>{movie.title}</h2>
      <h5>Sorti le : {formattedDate}</h5>
      <h4>{movie.vote_average}/10</h4>
      <ul className="tag">
        {movie.genre_ids.map((id) => (
          <li key={id}>{getGenreNameById(id)}</li>
        ))}
      </ul>
      <h3>Synopsis</h3>
      <p>{movie.overview}</p>
      <form>
        <button className="btn" id="favorite" onClick={(e) => saveMovie(e)}>
          Ajouter aux favoris
        </button>
      </form>
    </li>
  );
};

export default Card;
