import React, { useEffect, useState } from "react";
import Liked from "./Liked";
import axios from "axios";

const Favorite = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const storedMovies = JSON.parse(localStorage.getItem("favMovies"));
      if (storedMovies && storedMovies.length > 0) {
        const fetchedData = [];
        for (const movie of storedMovies) {
          try {
            const response = await axios.get(
              `https://api.themoviedb.org/3/movie/` +
                movie.id +
                `?api_key=ed82f4c18f2964e75117c2dc65e2161d`
            );
            fetchedData.push(response.data);
          } catch (error) {
            console.error("Error fetching movie data:", error);
          }
        }
        setData(fetchedData);
      }
    };

    fetchData();
  }, []);

  const deleteMovie = (movieId) => {
    const updatedFavMovies = data.filter((movie) => movie.id !== movieId);
    setData(updatedFavMovies);
    localStorage.setItem(
      "favMovies",
      JSON.stringify(updatedFavMovies.map((movie) => ({ id: movie.id })))
    );
  };

  return (
    <ul className="result">
      {data.map((movie, index) => (
        <Card key={index} movie={movie} deleteMovie={deleteMovie} />
      ))}
    </ul>
  );
};

export default Favorite;
