import React, { useEffect, useState } from "react";
import Navigation from "../components/Navigation";
import axios from "axios";
import Card from "../components/Card";

const Favoris = () => {
  const [listData, setListData] = useState([]);

  useEffect(() => {
    let moviesId = window.localStorage.movies
      ? window.localStorage.movies.split(",")
      : [];

    // CrÃ©er un tableau de promesses pour les requÃªtes Axios
    const requests = moviesId.map((id) =>
      axios.get(
        `https://api.themoviedb.org/3/movie/${id}?api_key=ed82f4c18f2964e75117c2dc65e2161d&language=fr-FR`
      )
    );

    // Attendre que toutes les requÃªtes soient terminÃ©es
    Promise.all(requests)
      .then((responses) => {
        // Mettre Ã  jour l'Ã©tat avec les donnÃ©es des rÃ©ponses
        setListData(responses.map((res) => res.data));
      })
      .catch((error) => {
        console.error("Error fetching movie data:", error);
      });
  }, []);

  return (
    <div className="user-list-page">
      <Navigation />
      <h2>
        Coups de coeur <span>ðŸ’–</span>
      </h2>
      <div className="result">
        {listData.length > 0 ? (
          listData.map((movie) => <Card movie={movie} key={movie.id} />)
        ) : (
          <h2>Aucun coup de coeur pour le moment</h2>
        )}
      </div>
    </div>
  );
};

export default Favoris;
