import React, { useEffect, useState } from "react";
import Navigation from "../components/Navigation";
import axios from "axios";
import Card from "../components/Card";

const Favoris = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const storedMovies = JSON.parse(localStorage.getItem("favMovies"));
      if (storedMovies && storedMovies.length > 0) {
        const fetchedData = [];
        for (const movie of storedMovies) {
          try {
            const response = await axios.get(
              `https://api.themoviedb.org/3/movie/` +
                movie.id +
                `?api_key=ed82f4c18f2964e75117c2dc65e2161d`
            );
            fetchedData.push(response.data);
          } catch (error) {
            console.error("Error fetching movie data:", error);
          }
        }
        setData(fetchedData);
      }
    };

    fetchData();
  }, []);
  return (
    <div>
      <Navigation />
      <div className="result">
        {data.length > 0 ? (
          data.map((movie) => <Card movie={movie} key={movie.id} />)
        ) : (
          <h2>Aucun coup de coeur pour le moment</h2>
        )}
      </div>
    </div>
  );
};

export default Favoris;
