import React, { useEffect, useState } from "react";
import Navigation from "../components/Navigation";
import axios from "axios";
import Card from "../components/Card";

const Favoris = () => {
  const [listData, setListData] = useState([]);

  useEffect(() => {
    let moviesId = window.localStorage.movies
      ? window.localStorage.movies.split(",")
      : [];
    // CrÃ©er un ensemble pour stocker les IDs uniques
    const uniqueIds = new Set(moviesId);

    // Fonction rÃ©cursive pour traiter les requÃªtes une par une
    const fetchData = async () => {
      for (let id of uniqueIds) {
        try {
          const res = await axios.get(
            `https://api.themoviedb.org/3/movie/${id}?api_key=ed82f4c18f2964e75117c2dc65e2161d&language=fr-FR`
          );
          // Mettre Ã  jour l'Ã©tat avec la nouvelle donnÃ©e
          setListData((listData) => [...listData, res.data]);
        } catch (error) {
          console.error("Error fetching movie data:", error);
        }
      }
    };

    fetchData();
  }, []);
  return (
    <div className="user-list-page">
      <Navigation />
      <h2>
        Coups de coeur <span>ðŸ’–</span>
      </h2>
      <div className="result">
        {listData.length > 0 ? (
          listData.map((movie) => <Card movie={movie} key={movie.id} />)
        ) : (
          <h2>Aucun coup de coeur pour le moment</h2>
        )}
      </div>
    </div>
  );
};

export default Favoris;
