import React, { useEffect, useState } from "react";
import Navigation from "../components/Navigation";
import axios from "axios";
import Card from "../components/Card";

const Favoris = () => {
  const [data, setData] = useState([]);

  useEffect(() => {
    let moviesId = window.localStorage.movies
      ? window.localStorage.movies.split(",")
      : [];

    let fetchData = (id) => {
      axios
        .get(
          `https://api.themoviedb.org/3/movie/${id}?api_key=ed82f4c18f2964e75117c2dc65e2161d&language=fr-FR`
        )
        .then((res) => {
          setData((prevData) => [...prevData, res.data]);
        })
        .catch((error) => {
          console.error("Error fetching movie:", error);
        });
    };

    // Effacer les données précédentes avant d'ajouter les nouvelles données
    setData([]);

    // Appeler fetchData pour chaque ID de film
    moviesId.forEach((id) => {
      fetchData(id);
    });
  }, []);

  return (
    <div>
      <Navigation />
      <div className="result">
        {data.length > 0 ? (
          data.map((movie) => <Card movie={movie} key={movie.id} />)
        ) : (
          <h2>Aucun coup de coeur pour le moment</h2>
        )}
      </div>
    </div>
  );
};

export default Favoris;
